import '../CSS/ROIForm.css';
import { useEffect, useState } from 'react';

import Countries from  '../Assets/Data/Countries';
function ROIForm({setShowCountryForm}){
	const [showForm, setShowForm] = useState(false);
	const [searchValue, setSearchValue] = useState('');
	const [countryArr, setCountryArr] = useState([]);

	useEffect(()=>{
		if(searchValue.length === 0 || searchValue === null || typeof searchValue === 'undefined'){
			var noSearchCopy = [...Countries];
			setCountryArr(noSearchCopy);
		}else{
			var copyArr = [...Countries];
			copyArr = copyArr.filter(country=>country.toLowerCase().includes(searchValue.toLowerCase()));

			setCountryArr(copyArr);

		}
	},[searchValue])

	return(

	<section className="ROI-Form">
		<button className='New-Season-Button' onClick={()=>setShowCountryForm(true)}>
			<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
				<path d="M24 9h-9v-9h-6v9h-9v6h9v9h6v-9h9z"/>
			</svg>

		</button>
		<button className='New-ROI-Button-Active'>
			<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
				<path d="M14.078 4.232l-12.64 12.639-1.438 7.129 7.127-1.438 12.641-12.64-5.69-5.69zm-10.369 14.893l-.85-.85 11.141-11.125.849.849-11.14 11.126zm2.008 2.008l-.85-.85 11.141-11.125.85.85-11.141 11.125zm18.283-15.444l-2.816 2.818-5.691-5.691 2.816-2.816 5.691 5.689z"/>
			</svg>
		</button>
		<h1 className='New-ROI-Title'>New ROI</h1>
		<section className='ROI-Container'>
			<div className='Country-Selector'>
				<input type="search" placeholder="Search.." value={searchValue} onChange={(e)=>setSearchValue(e.target.value)} className='Country-Selector-Search'/>
    			{countryArr.map(country=> <button key ={country} className='Selector-Button' onClick={()=>setShowForm(true)}>{country}</button>)}

			</div>
			<form className='New-ROI-Form'>
				<input type="text" placeholder='Candidates'/>
				<input type="text" placeholder='Permits Left'/>
				<input type="submit" className='ROI-Submit-Button'/>
			</form>
		</section>


	</section>
	);

}
export default ROIForm;
