import '../CSS/ROIForm.css';
import { useEffect, useState, useRef } from 'react';
import { getData } from '../HelperFunctions';
import axios from 'axios';

function ROIForm({setShowCountryForm}){
	const [showForm, setShowForm] = useState(false);
	const [searchValue, setSearchValue] = useState('');
	const [originalCountryArr, setOriginalCountryArr]= useState([]);
	const [countryArr, setCountryArr] = useState([]);
	const [initLoad, setInitLoad] = useState(true);

	const [name, setName] = useState('');
	const [candidates, setCandidates] = useState('');
	const [permits, setPermits] = useState('');
	const [date, setDate] = useState('');

	const [prevButton, setPrevButton] = useState();
	const containerRef = useRef();

	function toggleForm(e, country){
		setShowForm(prev=>!prev);
		if(e.currentTarget.className === 'Selector-Button'){
			e.currentTarget.className = 'Selector-Button-Active';
			setName(country);

			if(prevButton !== null || typeof prevButton !== 'undefined'){
				var localArr = containerRef.current.childNodes;
				for(let i=0; i <localArr.length; i++){
					if(localArr[i].id === prevButton){
						localArr[i].className = 'Selector-Button';
					}
				}

			}

			setPrevButton(e.currentTarget.id);

		}else{
			e.currentTarget.className = 'Selector-Button'
			setName('');
		}
	}

	async function onSubmit(e){
		e.preventDefault();

		axios.post('http://localhost:4000/roi/add', {
			country_name: name,
			country_candidates: candidates,
			country_permits: permits,
			country_date: date
		  })
		  .then(function (response) {
			console.log(response);
			setName('');
			setCandidates('');
			setPermits('');
			setDate('');
		  })
		  .catch(function (error) {
			console.log(error);
		  });
	}

	useEffect(()=>{
		if(initLoad){
			getData().then(res=>{
				console.log(res.data);
				setOriginalCountryArr(res.data);
				setCountryArr(res.data);

			})

			setInitLoad(false);	

		}
	},[]);

	useEffect(()=>{

		if(!initLoad){
			if(searchValue.length === 0 || searchValue === null || typeof searchValue === 'undefined'){
				console.log('no search text');
				var noSearchCopy = [...originalCountryArr];
				setCountryArr(noSearchCopy);
			}else{
				var copyArr = [...originalCountryArr];
				copyArr = copyArr.filter(country=>country.country_name.toLowerCase().includes(searchValue.toLowerCase()));

				setCountryArr(copyArr);

			}
		}

	},[searchValue])

	return(

	<section className="ROI-Form">
		<button className='Season-Button' onClick={()=>setShowCountryForm(true)}>Season</button>
		<button className='ROI-Button-Active'>ROI</button>
		<button className='New-ROI-Button-Active'>
			<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
				<path d="M24 9h-9v-9h-6v9h-9v6h9v9h6v-9h9z"/>
			</svg>

		</button>
		<button className='Edit-ROI-Button'>
			<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
				<path d="M14.078 4.232l-12.64 12.639-1.438 7.129 7.127-1.438 12.641-12.64-5.69-5.69zm-10.369 14.893l-.85-.85 11.141-11.125.849.849-11.14 11.126zm2.008 2.008l-.85-.85 11.141-11.125.85.85-11.141 11.125zm18.283-15.444l-2.816 2.818-5.691-5.691 2.816-2.816 5.691 5.689z"/>
			</svg>
		</button>
		<button className='Delete-ROI-Button'>
			<svg width="24" height="24" xmlns="http://www.w3.org/2000/svg" fillRule="evenodd" clipRule="evenodd">
				<path d="M19 24h-14c-1.104 0-2-.896-2-2v-16h18v16c0 1.104-.896 2-2 2m-9-14c0-.552-.448-1-1-1s-1 .448-1 1v9c0 .552.448 1 1 1s1-.448 1-1v-9zm6 0c0-.552-.448-1-1-1s-1 .448-1 1v9c0 .552.448 1 1 1s1-.448 1-1v-9zm6-5h-20v-2h6v-1.5c0-.827.673-1.5 1.5-1.5h5c.825 0 1.5.671 1.5 1.5v1.5h6v2zm-12-2h4v-1h-4v1z"/>
			</svg>
		</button>
		<h1 className='New-ROI-Title'>New ROI</h1>
		<section className='ROI-Container'>
			<div className='Country-Selector' ref={containerRef}>
				<input type="search" placeholder="Search.." value={searchValue} onChange={(e)=>setSearchValue(e.target.value)} className='Country-Selector-Search'/>
    			{countryArr.map(country=> <button key ={country.country_name} className='Selector-Button' id={country._id} onClick={(e)=>toggleForm(e, country.country_name)}>{country.country_name}</button>)}

			</div>
			<form className='New-ROI-Form'>
				{showForm?<>
				<input type="text" placeholder='Candidates' value={candidates} onChange={(e)=>setCandidates(e.currentTarget.value)}/>
				<input type="text" placeholder='Permits Left' value={permits} onChange={(e)=>setPermits(e.currentTarget.value)}/>
				<input type="date" placeholder='Date' value={date} onChange={(e)=>setDate(e.currentTarget.value)}/>
				<input type="submit" className='ROI-Submit-Button' onClick={(e)=>onSubmit(e)}/>
				</>:null}
			</form>
		</section>


	</section>
	);

}
export default ROIForm;
