import '../CSS/Search.css';
import Countries from '../Assets/Data/Countries';
import { useState } from 'react';

function Search(){
	const [searchValue, setSearchValue] = useState('');

	const emptySearch = <ul>{Countries.sort().splice(0,5).map(country=>{return <li key ={country}>{country}</li>})}</ul>;
	const notEmptySearch = <ul>{Countries.sort().splice(0,5).map(country=>{return <li key ={country}>{country}</li>})}</ul>;

	function handleChange(e){
		setSearchValue(e.target.value)
	}

	return(
		<div className="Search-Container">
			<input 
			type='search' 
			className="Search-Bar" 
			placeholder='Search your country here...'
			value = {searchValue}
			onChange={(e)=>handleChange(e)}
			/>
			<div className='Search-Dropdown'>

				{searchValue === null || typeof searchValue === 'undefined'? emptySearch : notEmptySearch}

			</div>
		</div>
	);
}

export default Search;
